library(shiny)
library(palmerpenguins)

ui &lt;- page_sidebar(
  selectInput("variable",
            "Variable:",
            c("Species" = "species",
              "Island" = "island",
              "Bill Length" = "bill_length_mm"),
            selected = "species"),
  renderText("summary")

)


server &lt;- function(input, output) {

  x &lt;- reactive({
    penguins[[input$variable]]
  })
  output$summary &lt;- shiny::renderText({ 
    summary(penguin_data())
  })

}

shinyApp(ui = ui, server = server)
